mapper.select:
SELECT *
FROM (
SELECT ROWNUM AS rn, m.*
FROM (
SELECT *
FROM members
WHERE UPPER(user_id) LIKE UPPER(#{keyword})
OR UPPER(username) LIKE UPPER(#{keyword})
OR UPPER(email) LIKE UPPER(#{keyword})
ORDER BY ${sortField} ${sortDirection}
) m
WHERE ROWNUM <= #{endRow}
)
WHERE rn > #{startRow}
